import shortid from 'shortid';

const json = window.localStorage.getItem('albums');
let albums = null;
if(json) {
  albums = JSON.parse(json);
}
else {
  save();
}

function save() {
  window.localStorage.setItem('albums', JSON.stringify(albums));
}

